    start_urls=[]
    city_codes={}

    def __init__(self, name=None, **kwargs):
        Spider.__init__(self, name)

        self.dbpool = adbapi.ConnectionPool('MySQLdb',
            db = 'driving',
            user = 'root',
            passwd = '123456',
            cursorclass = MySQLdb.cursors.DictCursor,
            charset = 'utf8',
            use_unicode = False
        )

        specialCities = [110000, 120000, 310000, 500000];
        cities = json.loads(self.jsonStr)
        for city in cities:
            if city['parent'] or (city['code'] in specialCities):
                self.start_urls.append(''.join(['http://jiaxiao.jiaxiaozhijia.com/',city['pinyin']]))
                self.city_codes[city['pinyin']] = city['code']

        #print self.start_urls

    def parse(self,response):
        hxs=Selector(response)
        items=[]
        allSite=hxs.xpath('//ul/li[1]/dl/dt/a')
        if len(allSite)>0:
            url = response.url
            province = os.path.basename(url)
            item=ProvinceItem()
            item['link'] = allSite.xpath('@href').extract()[0]
            item['code'] = self.city_codes.get(province)
            items.append(item)

        sites=hxs.xpath('//ul/li[1]/dl/dd/a')
        for site in sites:
            item=ProvinceItem()
            item['name'] = site.xpath('text()').extract()[0]
            item['link'] = site.xpath('@href').extract()[0]
            item['code'] = int(os.path.basename(item['link']).split('-')[0])
            item['parent'] = item['code'] - item['code'] % 100
            item['pinyin'] = ''.join(lazy_pinyin(item['name']))
            items.append(item)
            #self.saveCity(item);
         
        for item in items:
            yield Request(item['link'],meta={'code':item['code']},callback=self.parse2)

    def parse2(self,response):
        hxs=Selector(response)
        sites=hxs.xpath('//div[@class="jiakaobaodian-pull-left jiaxiao-logo"]')
        parentCode = response.meta['code']
        url = response.url
        items=[]
        if len(sites)>0:
            for site in sites:
                item=UrlItem()
                item['url'] = site.xpath('a/@href').extract()[0]
                districtCode = int(os.path.basename(url).split('-')[0])
                item['district'] = parentCode if districtCode==0 else districtCode;
                items.append(item)

        if url.endswith('1.html'):
            pages=hxs.xpath('//ul[@class="paging"]/li/a')
            pageCount = len(pages)
            if pageCount>2:
                lastpos = pageCount - 2
                for i, page in enumerate(pages):
                    if i>0 and i<=lastpos:
                        url = page.xpath('@href').extract()[0]
                        items.extend([self.make_requests_from_url(url).replace(meta={'code':parentCode}, callback=self.parse2)]) 

        return items

    def saveCity(self, item):
        self.dbpool.runInteraction(self._conditional_insert, item)
        return item

    def _conditional_insert(self, tx, item):
        tx.execute('insert into cities values (%s, %s, %s, %s, %s)', ('NULL', item['parent'], item['name'], item['code'], item['pinyin']))